const o=document.getElementById("darkModeToggle"),t=document.documentElement;try{const e=localStorage.getItem("theme");e==="dark"||!e&&window.matchMedia("(prefers-color-scheme: dark)").matches?t.classList.add("dark"):t.classList.remove("dark")}catch(e){console.warn("Could not set initial theme due to an error:",e)}o&&o.addEventListener("click",()=>{try{const e=t.classList.toggle("dark");localStorage.setItem("theme",e?"dark":"light")}catch(e){console.warn("Could not save theme preference to localStorage:",e)}});
